&lt;li&gt;&lt;strong&gt;利用grid布局&lt;/strong&gt;&lt;/li&gt;
&lt;/ol&gt;
&lt;figure class=&#34;highlight css&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;selector-class&#34;&gt;.parent&lt;/span&gt; &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;width&lt;/span&gt;: &lt;span class=&#34;number&#34;&gt;100vw&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;height&lt;/span&gt;: &lt;span class=&#34;number&#34;&gt;100vh&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;display&lt;/span&gt;: grid;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;selector-class&#34;&gt;.child&lt;/span&gt; &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    justify-self: center;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;align-self&lt;/span&gt;: center;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;ol&gt;
&lt;li&gt;&lt;strong&gt;flex+margin&lt;/strong&gt;&lt;/li&gt;
&lt;/ol&gt;
&lt;figure class=&#34;highlight css&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;selector-class&#34;&gt;.parent&lt;/span&gt; &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;width&lt;/span&gt;: &lt;span class=&#34;number&#34;&gt;100vw&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;height&lt;/span&gt;: &lt;span class=&#34;number&#34;&gt;100vh&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;display&lt;/span&gt;: flex;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;selector-class&#34;&gt;.child&lt;/span&gt; &amp;#123;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;attribute&#34;&gt;margin&lt;/span&gt;: auto;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&amp;#125;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;ol&gt;
&lt;li&gt;&lt;strong&gt;JS 模拟CSS实现&lt;/strong&gt;&lt;/li&gt;
&lt;/ol&gt;
&lt;figure class=&#34;highlight js&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;6&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;7&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;8&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;9&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;10&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;11&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;12&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;13&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;14&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;15&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;16&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;//方案一:&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;var&lt;/span&gt; div = &lt;span class=&#34;built_in&#34;&gt;document&lt;/span&gt;.querySelector(&lt;span class=&#34;string&#34;&gt;&amp;#x27;div&amp;#x27;&lt;/span&gt;);&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;var&lt;/span&gt; htmlW = &lt;span class=&#34;built_in&#34;&gt;document&lt;/span&gt;.documentElement.clientWidth&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;keyword&#34;&gt;var&lt;/span&gt; htmlH = &lt;span class=&#34;built_in&#34;&gt;document&lt;/span&gt;.documentElement.clientHeight&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;div.style.position = &lt;span class=&#34;string&#34;&gt;&amp;#x27;absolute&amp;#x27;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;comment&#34;&gt;// 2. 设置left  left = 屏幕宽度的一半 - 盒子宽度的一半&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;div.style.left = (htmlW - div.offsetWidth) / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt; + &lt;span class=&#34;string&#34;&gt;&amp;#x27;px&amp;#x27;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;// 3. 设置top&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;div.style.top = (htmlH - div.offsetHeight) / &lt;span class=&#34;number&#34;&gt;2&lt;/span&gt; + &lt;span class=&#34;string&#34;&gt;&amp;#x27;px&amp;#x27;&lt;/span&gt;;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;//方案二:&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;//	divW = div.getBoundingClientRect().width&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;//	divH = div.getBoundingClientRect().height&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;//  div.style.left = (htmlW - divW) / 2 + &amp;#x27;px&amp;#x27;;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;comment&#34;&gt;//	div.style.top = (htmlH - divH) / 2 + &amp;#x27;px&amp;#x27;;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;

 &lt;link rel=&#34;stylesheet&#34; class=&#34;aplayer-secondary-style-marker&#34; href=&#34;\assets\css\APlayer.min.css&#34;&gt;&lt;script src=&#34;\assets\js\APlayer.min.js&#34; class=&#34;aplayer-secondary-script-marker&#34;&gt;&lt;/script&gt;&lt;h3 id=&#34;CSS基础知识&#34;&gt;&lt;a href=&#34;#CSS基础知识&#34; class=&#34;headerlink&#34; title=&#34;CSS基础知识&#34;&gt;&lt;/a&gt;CSS基础知识&lt;/h3&gt;&lt;h4 id=&#34;绝对路径和相对路径&#34;&gt;&lt;a href=&#34;#绝对路径和相对路径&#34; class=&#34;headerlink&#34; title=&#34;绝对路径和相对路径&#34;&gt;&lt;/a&gt;绝对路径和相对路径&lt;/h4&gt;&lt;ol&gt;
&lt;li&gt;绝对路径&lt;/li&gt;
&lt;/ol&gt;
&lt;figure class=&#34;highlight plaintext&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;协议名://主机名:端口号/路径&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;schema://host:port/path&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;当跳转目标和当前页面的协议相同时，可以省略协议&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
&lt;ol&gt;
&lt;li&gt;相对路径&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;以./开头，./表示当前资源所在的目录&lt;/p&gt;
&lt;p&gt;可以书写../表示返回上一级目录&lt;/p&gt;
&lt;p&gt;相对路径中：./可以省略&lt;/p&gt;
&lt;h4 id=&#34;伪类选择器，选中某些元素的某种状态&#34;&gt;&lt;a href=&#34;#伪类选择器，选中某些元素的某种状态&#34; class=&#34;headerlink&#34; title=&#34;伪类选择器，选中某些元素的某种状态&#34;&gt;&lt;/a&gt;伪类选择器，选中某些元素的某种状态&lt;/h4&gt;&lt;p&gt;1）link: 超链接未访问时的状态&lt;/p&gt;
&lt;p&gt;2）visited: 超链接访问过后的状态&lt;/p&gt;
&lt;p&gt;3）hover: 鼠标悬停状态&lt;/p&gt;
&lt;p&gt;4）active：激活状态，鼠标按下状态&lt;/p&gt;
&lt;p&gt;定义顺序：（爱恨法则）love hate&lt;/p&gt;
&lt;h4 id=&#34;选择器组合&#34;&gt;&lt;a href=&#34;#选择器组合&#34; class=&#34;headerlink&#34; title=&#34;选择器组合&#34;&gt;&lt;/a&gt;选择器组合&lt;/h4&gt;&lt;ul&gt;
&lt;li&gt;后代选择器(以空格   分隔)  （所有符合的子孙后代）&lt;/li&gt;
&lt;li&gt;子元素选择器(以大于 &lt;strong&gt;&amp;gt;&lt;/strong&gt; 号分隔）（仅亲儿子）&lt;/li&gt;
&lt;li&gt;相邻兄弟选择器（以加号 &lt;strong&gt;+&lt;/strong&gt; 分隔）&lt;/li&gt;
&lt;li&gt;普通兄弟选择器（以波浪号 &lt;strong&gt;～&lt;/strong&gt; 分隔）&lt;/li&gt;
&lt;/ul&gt;
&lt;h4 id=&#34;选择器权重（X-X-X-X-）&#34;&gt;&lt;a href=&#34;#选择器权重（X-X-X-X-）&#34; class=&#34;headerlink&#34; title=&#34;选择器权重（X X X X ）&#34;&gt;&lt;/a&gt;选择器权重（X X X X ）&lt;/h4&gt;&lt;ol&gt;
&lt;li&gt;千位：如果是内联样式，记1，否则记0&lt;/li&gt;
&lt;li&gt;百位：等于选择器中所有id选择器的数量&lt;/li&gt;
&lt;li&gt;十位：等于选择器中所有类选择器、属性选择器、伪类选择器的数量&lt;/li&gt;
&lt;li&gt;个位：等于选择器中所有元素选择器、伪元素选择器的数量&lt;br&gt;&lt;strong&gt;同权重下：&lt;/strong&gt;&lt;br&gt;内联样式(标签内部) &amp;gt; 嵌入样式表(当前文件中) &amp;gt; 外部样式表(外部文件中)&lt;br&gt;！import &amp;gt; id &amp;gt; class &amp;gt;tag&lt;br&gt;备注：important比内联优先级高&lt;/li&gt;
&lt;/ol&gt;
&lt;h4 id=&#34;属性值的计算过程&#34;&gt;&lt;a href=&#34;#属性值的计算过程&#34; class=&#34;headerlink&#34; title=&#34;属性值的计算过程&#34;&gt;&lt;/a&gt;属性值的计算过程&lt;/h4&gt;&lt;p&gt;&lt;img src=&#34;/images/20210522231135.jpg&#34; alt=&#34;&#34;&gt;&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;1.确定声明值：参考样式表中没有冲突的声明，作为CSS属性值&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;2.层叠冲突：对样式表有冲突的声明使用层叠规则，确定CSS属性值&lt;/strong&gt;&lt;br&gt;        比较重要性-&amp;gt;比较特殊性-&amp;gt;比较源次序&lt;br&gt;&lt;strong&gt;3.使用继承：对仍然没有值的属性，若可以继承，则继承父元素的值&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;4.使用默认值：对仍然没有值的属性，使用默认值&lt;/strong&gt;&lt;/p&gt;
&lt;h3 id=&#34;盒模型&#34;&gt;&lt;a href=&#34;#盒模型&#34; class=&#34;headerlink&#34; title=&#34;盒模型&#34;&gt;&lt;/a&gt;盒模型&lt;/h3&gt;&lt;h4 id=&#34;行盒&#34;&gt;&lt;a href=&#34;#行盒&#34; class=&#34;headerlink&#34; title=&#34;行盒&#34;&gt;&lt;/a&gt;行盒&lt;/h4&gt;&lt;p&gt;常见的行盒：包含具体内容的元素  display：inline&lt;/p&gt;
&lt;p&gt;span、strong、em、i、img、video、audio&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;显著特点&lt;/strong&gt;&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;&lt;p&gt;盒子沿着内容沿伸&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;行盒不能设置宽高&lt;br&gt;调整行盒的宽高，应该使用字体大小、行高、字体类型，间接调整。&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;内边距、边框、外边距&lt;br&gt;水平方向有效，垂直方向不会实际占据空间。&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;行盒可被折断，因为内容可被折断换行（可通过word-break属性设置截断位置）&lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;空白折叠的规则仅适用于行盒内部和行盒之间 &lt;/p&gt;
&lt;/li&gt;
&lt;li&gt;&lt;p&gt;行盒与行盒之间的距离可使用水平方向上的margin和padding进行设置&lt;/p&gt;
&lt;/li&gt;
&lt;/ol&gt;
&lt;h4 id=&#34;行块盒&#34;&gt;&lt;a href=&#34;#行块盒&#34; class=&#34;headerlink&#34; title=&#34;行块盒&#34;&gt;&lt;/a&gt;行块盒&lt;/h4&gt;&lt;p&gt;display：inline-block &lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;不独占一行&lt;/li&gt;
&lt;li&gt;盒模型中所有尺寸都有效&lt;/li&gt;
&lt;/ol&gt;
&lt;h4 id=&#34;块盒&#34;&gt;&lt;a href=&#34;#块盒&#34; class=&#34;headerlink&#34; title=&#34;块盒&#34;&gt;&lt;/a&gt;块盒&lt;/h4&gt;&lt;p&gt;display：block&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;显著特点&lt;/strong&gt;&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;每个块盒的总宽度，必须刚好等于包含块的宽度&lt;/li&gt;
&lt;li&gt;会独占一行&lt;/li&gt;
&lt;li&gt;设置宽高有效&lt;/li&gt;
&lt;li&gt;两个常规流块盒，上下外边距相邻，会进行合并。两个外边距取最大值。&lt;/li&gt;
&lt;/ol&gt;
&lt;h4 id=&#34;可替换、非可替换元素&#34;&gt;&lt;a href=&#34;#可替换、非可替换元素&#34; class=&#34;headerlink&#34; title=&#34;可替换、非可替换元素&#34;&gt;&lt;/a&gt;可替换、非可替换元素&lt;/h4&gt;&lt;p&gt;大部分元素，页面上显示的结果，取决于元素内容，称为&lt;strong&gt;非可替换元素&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;少部分元素，页面上显示的结果，取决于元素属性，称为&lt;strong&gt;可替换元素&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;可替换元素：img、video、audio，绝大部分可替换元素均为行盒。&lt;/p&gt;
&lt;p&gt;可替换元素类似于行块盒，盒模型中所有尺寸都有效。&lt;/p&gt;
&lt;h4 id=&#34;盒模型应用&#34;&gt;&lt;a href=&#34;#盒模型应用&#34; class=&#34;headerlink&#34; title=&#34;盒模型应用&#34;&gt;&lt;/a&gt;盒模型应用&lt;/h4&gt;&lt;p&gt;改变宽高范围：box-sizing&lt;/p&gt;
&lt;p&gt;改变背景覆盖范围：background-clip（默认背景覆盖边框盒）&lt;/p&gt;
&lt;p&gt;溢出处理：overflow，控制内容溢出边框盒后的处理方式&lt;/p&gt;
&lt;p&gt;断词规则：word-break，会影响文字在什么位置被截断换行，几种取值：&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;keep-all：所有“单词”一律不拆分换行，只有空格可以触发自动换行&lt;/li&gt;
&lt;li&gt;break-all：所有单词碰到边界一律拆分换行&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;空白处理：white-space，控制空白字符的显示，同时能控制是否自动换行&lt;/p&gt;
&lt;ul&gt;
&lt;li&gt;nowrap：不换行&lt;/li&gt;
&lt;/ul&gt;
&lt;p&gt;&lt;img src=&#34;/images/20210523112448.jpg&#34; alt=&#34;&#34;&gt;&lt;/p&gt;
&lt;h3 id=&#34;视觉格式化模型&#34;&gt;&lt;a href=&#34;#视觉格式化模型&#34; class=&#34;headerlink&#34; title=&#34;视觉格式化模型&#34;&gt;&lt;/a&gt;视觉格式化模型&lt;/h3&gt;&lt;p&gt;视觉格式化模型，大体上将页面中盒子的排列分为三种方式：&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;常规流&lt;/li&gt;
&lt;li&gt;浮动：float&lt;/li&gt;
&lt;li&gt;定位：position&lt;/li&gt;
&lt;/ol&gt;
&lt;h4 id=&#34;浮动&#34;&gt;&lt;a href=&#34;#浮动&#34; class=&#34;headerlink&#34; title=&#34;浮动&#34;&gt;&lt;/a&gt;浮动&lt;/h4&gt;&lt;p&gt;应用场景：文字环绕，横向排列&lt;/p&gt;
&lt;p&gt;&lt;strong&gt;盒子尺寸&lt;/strong&gt;&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;宽度为auto时，适应内容宽度&lt;/li&gt;
&lt;li&gt;高度为auto时，与常规流一致，适应内容的高度&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;&lt;strong&gt;盒子排列&lt;/strong&gt;&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;浮动盒子在包含块中排列时，会避开常规流块盒&lt;/li&gt;
&lt;li&gt;常规流块盒在排列时，无视浮动盒子（对前面常规流块盒没影响，对后面有）&lt;/li&gt;
&lt;li&gt;行盒在排列时，会避开浮动盒子&lt;/li&gt;
&lt;li&gt;外边距合并不会发生&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;&lt;strong&gt;高度坍塌&lt;/strong&gt;&lt;/p&gt;
&lt;p&gt;常规流盒子的自动高度，在计算时，不会考虑包含的浮动盒子&lt;/p&gt;
&lt;p&gt;清除浮动：clear属性&lt;/p&gt;
&lt;h4 id=&#34;定位&#34;&gt;&lt;a href=&#34;#定位&#34; class=&#34;headerlink&#34; title=&#34;定位&#34;&gt;&lt;/a&gt;定位&lt;/h4&gt;&lt;p&gt;定位元素会脱离文档流（相对定位除外）&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;文档流中的元素摆放时，会忽略脱离了文档流的元素&lt;/li&gt;
&lt;li&gt;文档流中元素计算自动高度时，会忽略脱离了文档流的元素&lt;/li&gt;
&lt;/ol&gt;
&lt;h5 id=&#34;相对定位&#34;&gt;&lt;a href=&#34;#相对定位&#34; class=&#34;headerlink&#34; title=&#34;相对定位&#34;&gt;&lt;/a&gt;&lt;strong&gt;相对定位&lt;/strong&gt;&lt;/h5&gt;&lt;p&gt;不会导致元素脱离文档流，只是让元素在原来位置上进行偏移。偏移不会对其他盒子造成任何影响&lt;/p&gt;
&lt;h5 id=&#34;绝对定位&#34;&gt;&lt;a href=&#34;#绝对定位&#34; class=&#34;headerlink&#34; title=&#34;绝对定位&#34;&gt;&lt;/a&gt;&lt;strong&gt;绝对定位&lt;/strong&gt;&lt;/h5&gt;&lt;p&gt;包含块变化：找祖先中第一个定位元素，该元素的填充盒为其包含块。若找不到，则它的包含块为整个网页（初始化包含块）&lt;/p&gt;
&lt;h5 id=&#34;固定定位&#34;&gt;&lt;a href=&#34;#固定定位&#34; class=&#34;headerlink&#34; title=&#34;固定定位&#34;&gt;&lt;/a&gt;&lt;strong&gt;固定定位&lt;/strong&gt;&lt;/h5&gt;&lt;p&gt;包含块不同：固定为视口（浏览器的可视窗口）&lt;/p&gt;
&lt;h5 id=&#34;定位下的居中&#34;&gt;&lt;a href=&#34;#定位下的居中&#34; class=&#34;headerlink&#34; title=&#34;定位下的居中&#34;&gt;&lt;/a&gt;定位下的居中&lt;/h5&gt;&lt;p&gt;某个方向居中：&lt;/p&gt;
&lt;ol&gt;
&lt;li&gt;定宽（高）&lt;/li&gt;
&lt;li&gt;将左右（上下）距离设置为0&lt;/li&gt;
&lt;li&gt;将左右（上下）margin设置为auto&lt;/li&gt;
&lt;/ol&gt;
&lt;p&gt;绝对定位和固定定位中，margin为auto时，会自动吸收剩余空间&lt;/p&gt;
&lt;h4 id=&#34;堆叠上下文&#34;&gt;&lt;a href=&#34;#堆叠上下文&#34; class=&#34;headerlink&#34; title=&#34;堆叠上下文&#34;&gt;&lt;/a&gt;&lt;strong&gt;堆叠上下文&lt;/strong&gt;&lt;/h4&gt;&lt;p&gt;设置z-index，通常情况下，该值越大，越靠近用户&lt;/p&gt;
&lt;h5 id=&#34;补充&#34;&gt;&lt;a href=&#34;#补充&#34; class=&#34;headerlink&#34; title=&#34;补充&#34;&gt;&lt;/a&gt;&lt;strong&gt;补充&lt;/strong&gt;&lt;/h5&gt;&lt;ul&gt;
&lt;li&gt;绝对定位、固定定位元素一定是块盒&lt;/li&gt;
&lt;li&gt;绝对定位、固定定位元素一定不是浮动&lt;/li&gt;
&lt;li&gt;没有外边距合并&lt;/li&gt;
&lt;/ul&gt;

 &lt;link rel=&#34;stylesheet&#34; class=&#34;aplayer-secondary-style-marker&#34; href=&#34;\assets\css\APlayer.min.css&#34;&gt;&lt;script src=&#34;\assets\js\APlayer.min.js&#34; class=&#34;aplayer-secondary-script-marker&#34;&gt;&lt;/script&gt;&lt;h3 id=&#34;三栏布局&#34;&gt;&lt;a href=&#34;#三栏布局&#34; class=&#34;headerlink&#34; title=&#34;三栏布局&#34;&gt;&lt;/a&gt;三栏布局&lt;/h3&gt;&lt;p&gt;&lt;strong&gt;共同目的&lt;/strong&gt;：实现两侧宽度固定，中间宽度自适应的三栏布局，并且中间先加载渲染。&lt;/p&gt;
&lt;h4 id=&#34;一、双飞翼布局&#34;&gt;&lt;a href=&#34;#一、双飞翼布局&#34; class=&#34;headerlink&#34; title=&#34;一、双飞翼布局&#34;&gt;&lt;/a&gt;一、双飞翼布局&lt;/h4&gt;&lt;blockquote&gt;
&lt;p&gt;&lt;strong&gt;主要思路：&lt;/strong&gt;&lt;code&gt;浮动+负margin&lt;/code&gt;&lt;/p&gt;
&lt;p&gt;给主区域添加一个父元素，设置&lt;code&gt;width：100%&lt;/code&gt;，&lt;/p&gt;
&lt;p&gt;让左中右三个区域都左浮动，&lt;/p&gt;
&lt;p&gt;给主区域设置左右&lt;code&gt;margin&lt;/code&gt;缩进，留出空位给两边的区域，&lt;/p&gt;
&lt;p&gt;让左边区域&lt;code&gt;margin-left:-100%&lt;/code&gt;，右边区域&lt;code&gt;margin-left:-200px&lt;/code&gt;&lt;/p&gt;
&lt;/blockquote&gt;
&lt;figure class=&#34;highlight html&#34;&gt;&lt;table&gt;&lt;tr&gt;&lt;td class=&#34;gutter&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;1&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;2&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;3&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;4&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;5&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;td class=&#34;code&#34;&gt;&lt;pre&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;tag&#34;&gt;&amp;lt;&lt;span class=&#34;name&#34;&gt;div&lt;/span&gt; &lt;span class=&#34;attr&#34;&gt;class&lt;/span&gt;=&lt;span class=&#34;string&#34;&gt;&amp;quot;wrapper&amp;quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;  &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;    &lt;span class=&#34;tag&#34;&gt;&amp;lt;&lt;span class=&#34;name&#34;&gt;div&lt;/span&gt; &lt;span class=&#34;attr&#34;&gt;class&lt;/span&gt;=&lt;span class=&#34;string&#34;&gt;&amp;quot;main&amp;quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;main&lt;span class=&#34;tag&#34;&gt;&amp;lt;/&lt;span class=&#34;name&#34;&gt;div&lt;/span&gt;&amp;gt;&lt;/span&gt;&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;tag&#34;&gt;&amp;lt;/&lt;span class=&#34;name&#34;&gt;div&lt;/span&gt;&amp;gt;&lt;/span&gt;   &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;tag&#34;&gt;&amp;lt;&lt;span class=&#34;name&#34;&gt;div&lt;/span&gt; &lt;span class=&#34;attr&#34;&gt;class&lt;/span&gt;=&lt;span class=&#34;string&#34;&gt;&amp;quot;left&amp;quot;&lt;/span&gt;&amp;gt;&lt;/span&gt;left&lt;span class=&#34;tag&#34;&gt;&amp;lt;/&lt;span class=&#34;name&#34;&gt;div&lt;/span&gt;&amp;gt;&lt;/span&gt;   &lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;&lt;span class=&#34;tag&#34;&gt;&amp;lt;&lt;span class=&#34;name&#34;&gt;div&lt;/span&gt; &lt;span class=&#34;attr&#34;&gt;class&lt;/span&gt;=&lt;span class=&#34;string&#34;&gt;&amp;quot;right&amp;quot;&lt;/span&gt;&amp;